import java.util.Scanner;public class matTrans {	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int x;		int [][] Matrix = new int[3][3];		for (int i = 0; i < Matrix.length; ++i) {			for (int j = 0; j < Matrix[i].length; ++j) {				Matrix[i][j] = sc.nextInt();			}		}		System.out.println("Original Matrix: ");		for (int i = 0; i < Matrix.length; ++i) {			for (int j = 0; j < Matrix[i].length; ++j) {				System.out.print(Matrix[i][j] + " ");			}			System.out.println();		}		for (int i = 0; i < Matrix.length; ++i) {			for (int j = 0; j < Matrix[i].length; ++j) {				if (i < j) {					x = Matrix[i][j];					Matrix[i][j] = Matrix[j][i];					Matrix[j][i] = x;				}			}		}		System.out.println("Transposed Matrix: ");		for (int i = 0; i < Matrix.length; ++i) {			for (int j = 0; j < Matrix[i].length; ++j) {				System.out.print(Matrix[i][j] + " ");			}			System.out.println();		}	}}